mapper.select:
select
remittance_id, unit_register_id, employee_id, sa_id, deposit_base,
unit_deposite,employee_deposite,deposite_total
from group1_gjj.remittance


        and remittance_id = #{remittanceId}


        and unit_register_id = #{unitRegisterId}


        and employee_id = #{employeeId}


        and sa_id = #{saId}


        and deposit_base = #{depositBase}


        and unit_deposite = #{unitDeposite}


        and employee_deposite = #{employeeDeposite}


        and deposite_total = #{depositeTotal}
